mapper.update:
UPDATE apply
SET apply_state = "通过"
WHERE member_name = #{member_name,jdbcType=VARCHAR}
    AND repo_name = #{repo_name,jdbcType=VARCHAR}

mapper.select:
SELECT *
FROM apply
WHERE member_name = #{member_name,jdbcType=VARCHAR}
  AND repo_name = #{repo_name,jdbcType=VARCHAR}
ORDER BY id DESC LIMIT 1
